parameters.jsCode:
// The AI's response is in the 'output' property of the input JSON
const aiResponse = $json.output;

// Clean the string by removing the markdown fences and extra whitespace
const cleanedJsonString = aiResponse.replace(/```json\n|\n```/g, '').trim();

try {
  // Parse the clean JSON string
  const parsedJson = JSON.parse(cleanedJsonString);
  return parsedJson;
} catch (error) {
  // If parsing fails, return an error
  return { error: "AI response was not valid JSON after cleaning.", details: error.message, originalText: aiResponse };
}

