{
  "recipe_name": "string",
  "description": "string (a brief, enticing description of the dish)",
  "prep_time": number (in minutes),
  "cook_time": number (in minutes),
  "difficulty": "string (Easy, Medium, or Hard)",
  "cuisine_type": "string",
  "ingredients_json": [
    {"item": "string", "quantity": "string"}
  ],
  "instructions": [
    "string (step 1)",
    "string (step 2)"
  ]
}

